def test_input(data, nspin):
    '''Prepares pylibxc compatible input from the density data'''
    inp = {}

    rhoa = (data[:,0])
    rhob = (data[:,1])
    sigmaaa = (data[:,2])
    sigmaab = (data[:,3])
    sigmabb = (data[:,4])
    lapla = (data[:,5])
    laplb = (data[:,6])
    taua = (data[:,7])
    taub = (data[:,8])

    if nspin == 1:
        inp["rho"] = rhoa+rhob
        inp["sigma"] = sigmaaa + sigmabb + 2*sigmaab
        inp["lapl"] = lapla+laplb
        inp["tau"] = taua+taub
    else:
        inp["rho"] = numpy.zeros((rhoa.size,2))
        inp["rho"][:,0] = rhoa
        inp["rho"][:,1] = rhob
        
        inp["sigma"] = numpy.zeros((rhoa.size,3))
        inp["sigma"][:,0] = sigmaaa
        inp["sigma"][:,1] = sigmaab
        inp["sigma"][:,2] = sigmabb
        
        inp["lapl"] = numpy.zeros((data.shape[0],2))
        inp["lapl"][:,0] = lapla
        inp["lapl"][:,1] = laplb

        inp["tau"] = numpy.zeros((rhoa.size,2))
        inp["tau"][:,0] = taua
        inp["tau"][:,1] = taub

    return inp

